mapper.update:
update member set memberpwd=#{memberpwd},
membername=#{membername},profilesrc=#{profilesrc},
phone=#{phone},contents=#{contents},gender=#{gender}
where memberid=#{memberid}

update member set buygrade= ((select avg(score) from board_review where bwriter=#{memberid}, category='구인', rwriter= bapplicant) +(select avg(score) from board_review where applicant=#{memberid}, category='구직', rwriter=bwriter))/2
and selgrade= ((select avg(score) from board_review where bwriter=#{memberid}, category='구직', rwriter= bapplicant) +(select avg(score) from board_review where applicant=#{memberid}, category='구인', rwriter=bwriter))/2
where memberid=#{memberid}
